        // Both validators join the set.
        join_validator_set(&validator_1, validator_1_address);
        join_validator_set(&validator_2, validator_2_address);
        end_epoch();

        // Validator 2 failed proposal.
        let failed_proposer_indices = vector::empty<u64>();
